import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import BankDetails from './BankDetails';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [bankName, setBankName] = useState('');
  const [results, setResults] = useState([]);
  const [selectedBankGuid, setSelectedBankGuid] = useState(null);
  const [field1, setField1] = useState('');
  const [field2, setField2] = useState('');

  const inputRef = useRef(null);

  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data.action === 'accept') {
        setSelectedBankGuid(event.data.guid);
        setField1(event.data.field1);
        setField2(event.data.field2);
      }
    };

    window.addEventListener('message', handleMessage);

    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.contains(event.target)) {
        setResults([]);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLinkClick = () => {
    setShowForm(true);
  };

  const handleInputChange = async (event) => {
    setBankName(event.target.value);
    if (event.target.value) {
      try {
        const response = await fetch(`http://localhost:8080/institutions/typeahead?query=${event.target.value}`);
        const data = await response.json();
        setResults(data);
      } catch (error) {
        console.error(error);
      }
    } else {
      setResults([]);
    }
  };

  const openReactBWindow = (guid) => {
    window.open(`http://localhost:3005?guid=${guid}`, '_blank', 'width=600, height=400');
  };

  return (
    <div className="App">
      {/* ... */}
      <div className={`form-container ${showForm ? 'show-form' : ''}`} ref={inputRef}>
        <form onSubmit={e => e.preventDefault()}>
          {/* ... */}
          {results.length > 0 && (
            <ul className="autocomplete-results">
              {/* ... */}
            </ul>
          )}
        </form>
      </div>
      {/* ... */}
    </div>
  );
}

export default App;
